import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: "", password: "" });

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await axios.post("http://127.0.0.1:8000/login/", formData);
    localStorage.setItem("token", res.data.token);
    navigate("/dashboard");
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <form className="bg-white p-6 rounded shadow-md w-96" onSubmit={handleSubmit}>
        <h2 className="text-xl font-bold mb-4">Login</h2>
        <input name="email" type="email" placeholder="Email" onChange={handleChange} className="w-full p-2 border rounded mb-2"/>
        <input name="password" type="password" placeholder="Password" onChange={handleChange} className="w-full p-2 border rounded mb-2"/>
        <button type="submit" className="w-full bg-green-500 text-white py-2 rounded">Login</button>
      </form>
    </div>
  );
};

export default Login;
