import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: "", password: "" });

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await axios.post("http://127.0.0.1:8000/login/", formData);
    localStorage.setItem("token", res.data.token);
    navigate("/dashboard");
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <form className="bg-white p-6 rounded-lg shadow-md w-96">
        <h2 className="text-2xl font-bold text-center text-gray-700 mb-4">Login</h2>
        <input name="email" type="email" placeholder="Email" onChange={handleChange} 
          className="w-full p-2 border rounded mb-2 focus:ring-2 focus:ring-blue-400"/>
        <input name="password" type="password" placeholder="Password" onChange={handleChange} 
          className="w-full p-2 border rounded mb-2 focus:ring-2 focus:ring-blue-400"/>
        <button onClick={handleSubmit} className="w-full bg-blue-500 hover:bg-blue-600 text-white py-2 rounded">Login</button>
        <p className="text-sm text-gray-600 mt-3 text-center">
          Don't have an account? <span className="text-blue-500 cursor-pointer" onClick={() => navigate("/signup")}>Sign up</span>
        </p>
      </form>
    </div>
  );
};

export default Login;
