import React from "react";
import { Link, useNavigate } from "react-router-dom";

const Login = () => {
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    navigate("/dashboard"); // Replace with API authentication
  };

  return (
    <div className="flex h-screen bg-gradient-to-r from-purple-500 to-indigo-500 justify-center items-center">
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
        <h2 className="text-3xl font-bold text-center text-gray-800">Welcome Back!</h2>
        <p className="text-center text-gray-600 mb-6">Please enter your login details</p>
        <form onSubmit={handleLogin}>
          <div>
            <label className="block text-gray-700">Email</label>
            <input type="email" className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-primary" required />
          </div>
          <div className="mt-4">
            <label className="block text-gray-700">Password</label>
            <input type="password" className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-primary" required />
          </div>
          <p className="text-right text-sm text-blue-500 hover:underline cursor-pointer mt-2">Forgot password?</p>
          <button type="submit" className="w-full mt-6 bg-primary hover:bg-secondary text-white py-3 rounded-lg font-bold transition">
            Sign in
          </button>
        </form>
        <div className="text-center mt-4">
          <span>Donâ€™t have an account? </span>
          <Link to="/signup" className="text-accent hover:underline">Sign Up</Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
