import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import '/Users/sonaselvaraj/Documents/Capstone/AI Interviewer 2/AI_Interviewer/frontend/src/Login.css';

const Signup = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const [error, setError] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSignup = async (e) => {
    e.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      setError("Passwords do not match");
      return;
    }

    setError(""); // Clear previous errors

    const response = await fetch("http://localhost:8000/auth/signup/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        username: formData.username,
        email: formData.email,
        password: formData.password,
        confirm_password: formData.confirmPassword,  // ✅ Ensure FastAPI receives this correctly
      }),
    });

    const responseData = await response.json();

    if (response.ok) {
      navigate("/");
    } else {
      if (responseData.detail) {
        if (Array.isArray(responseData.detail)) {
          setError(responseData.detail.map((err) => err.msg).join(", "));
        } else {
          setError(responseData.detail);
        }
      } else {
        setError("Signup failed! Please try again.");
      }
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-500 to-purple-600">
      <div className="bg-white p-8 rounded-lg shadow-lg max-w-md w-full">
        <h2 className="text-3xl font-bold text-center text-gray-800">Sign Up</h2>


        <p className="mt-4 text-center text-gray-600">
          Already have an account?{" "}
          <Link to="/" className="text-blue-500 hover:underline">
            Login
          </Link>
        </p>

        <div className="login-container">
              <div className="login-box">

                <form className="login-form" onSubmit={handleSignup}>
                <h2 className="login-title">LOGIN</h2>
                  <input 
                    type="email" 
                    name="email" 
                    placeholder="Email" 
                    className="input-field" 
                    onChange={handleChange} 
                    required 
                  />
                  <input 
                    type="password" 
                    name="password" 
                    placeholder="Password" 
                    className="input-field" 
                    onChange={handleChange} 
                    required 
                  />
                  <button type="submit" className="login-btn">Login</button>
                  <p className="signup-link">
                  Don't have an account? 
                  <Link to="/signup" className="signup-text">Sign Up</Link>
                </p>
                </form>

              </div>
            </div>
      </div>
    </div>
  );
};

export default Signup;
