const handleSignup = async (e) => {
  e.preventDefault();
  const finalInterest =
    formData.interest_area === "Others"
      ? formData.custom_interest
      : formData.interest_area;

  const response = await fetch("http://localhost:8000/auth/signup/", { // Fixed URL
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      username: formData.username,
      email: formData.email,
      password: formData.password,
      experience: formData.experience,
      skills: formData.skills,
      interest_area: finalInterest,
    }),
  });

  if (response.ok) {
    navigate("/");
  } else {
    alert("Signup failed!");
  }
};
